mapper.select:
SELECT l.*, q.messageQueueName, s.subscriberApiUrl FROM MessageLog l INNER JOIN MessageQueueSubscriber s ON l.messageQueueSubscriberId = s.id INNER JOIN MessageQueueInfo q ON s.messageQueueId = q.id
q.messageQueueName = #{query.messageQueueName}
s.subscriberApiUrl = #{query.subscriberApiUrl}
AND s.isActive = true AND q.isActive = true
ORDER BY id LIMIT #{start}, #{pageSize}
